<template>
  <div class="iconfont" :class="['icon-'+type]" :style="{color:color,'font-size':fontSize}" @click="click()"></div>
</template>

<script>
export default {
  props: {
    /**
       * 图标类型
       */
    type: String,
    /**
       * 图标颜色
       */
    color: String,
    /**
       * 图标大小
       */
    size: Number
  },
  computed: {
    fontSize () {
      return `${this.size}px`
    }
  },
  methods: {
    click () {
      this.$emit('click')
    }
  }
}
</script>

